css:
.product__buy button {
color: #ffffff;
background-color: #8000ff;
border-color: #8000ff;
width: 100%;
}
.product__buy button:hover {
background: #000000;
border-color: #000000;
}
.product__short-stock {
display: none !important;
} 
#product-actions button.share, {
  display: none;
}
.product-card__buy button, .product-card__buy button:hover  {
background-color: #8000ff;
border-color: #8000ff;
  color: #fff;
}
#product-actions .mb-3, #product-actions .mb-3 +article {
  display: none
}
.banners-grid.my-3.my-md-4.my-lg-5 div.col-12.col-lg-6.mb-3.mb-lg-0 {
    max-width: 50%;
    flex: 0 0 50%;
    padding-right: 10px;	 
    padding-left: 10px;	
  }
#manutencao .container {
    display: flex !important;
    flex-direction: column;
    justify-content: center !important;
    align-items: center !important;
    text-align: center;
    font-size: 200%;
   }
#manutencao .container img {
    padding-bottom: 50px;
    width: auto;
    max-width: 450px;
}

.__pages_manutencao .header,
.__pages_manutencao .footer {
    display: none !important;
}

@media screen and (max-width: 480px) {
    #manutencao .container img {
        padding-bottom: 50px;
        max-width: 100% !important;
    }
}
.__pages_manutencao .header,
.__pages_manutencao .footer {
    display: none !important;
}
#product-actions .product__buy button {
  margin-top: 1rem
}
#footer-pages ul li:nth-child(3) {
  display: none;
}
@media(max-width: 767px) {
#page-home .banners-grid .col-12 {
  flex: 0 0 33.33333%;
  max-width: 33.33333%;
  padding-left: 2px;
  padding-right: 2px
}
}
.fwr img {
  width: 10%
}
